
public class Monthswitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  int Month=15;
  switch(Month)
  {
  case 1: System.out.println("Welcome to January - 31 days");break;
  case 2: System.out.println("Welcome to February - 28 days; 29 days in Leap Years");break;
  case 3: System.out.println("Welcome to March - 31 days");break;
  case 4: System.out.println("Welcome to April - 30 days");break;
  case 5: System.out.println("Welcome to May - 31 days");break;
  case 6: System.out.println("Welcome to June - 30 days");break;
  case 7: System.out.println("Welcome to July - 31 days");break;
  case 8: System.out.println("Welcome to August - 31 days");break;
  case 9: System.out.println("Welcome to September - 30 days");break;
  case 10: System.out.println("Welcome to October - 31 days");break;
  case 11: System.out.println("Welcome to November - 30 days");break;
  case 12: System.out.println("Welcome to December - 31 days ");break;
  default: System.out.println("This Number is not a Month");break;
  
	}

}
}

